import time, uuid, os
import jwt

APP_STORE_CONNECT_BUNDLE_ID = os.environ.get("APP_STORE_CONNECT_BUNDLE_ID")
APP_STORE_CONNECT_IAP_ISSUER_ID = os.environ.get("APP_STORE_CONNECT_IAP_ISSUER_ID")
APP_STORE_CONNECT_IAP_KEY_ID = os.environ.get("APP_STORE_CONNECT_IAP_KEY_ID")
APP_STORE_CONNECT_IAP_PRIVATE_KEY = os.environ.get("APP_STORE_CONNECT_IAP_PRIVATE_KEY")
APP_STORE_CONNECT_STORE_KIT_ENDPOINT = os.environ.get("APP_STORE_CONNECT_STORE_KIT_ENDPOINT")

issue_time = round(time.time())
expiration_time = issue_time + 60 * 60 # 1 hour expiration

jwt_token = ""

def encodeSignedData(signedData):
    return jwt.decode(signedData, APP_STORE_CONNECT_IAP_PRIVATE_KEY, options={"verify_signature": False})

def main():
    signedTransaction = "..wFqq9ibqVmlcxGmcBdFdK_ga6Q_MtLDJuDRAur8xpyXAUSdj7VOGbsQmUgTVUAa2XVh6XwkRjZj5OLc1X2ey6A"
    print(encodeSignedData(signedTransaction))

if __name__ == '__main__':
    main()

def getJWT():
    global jwt_token
    if jwt_token == "":
        # 首次生成
        return generateJWT()
    if (time.time() + 10 * 60 ) > expiration_time:
        # 快过期了
        return generateJWT()
    
    return jwt_token


def generateJWT():
    header = {
    'alg': 'ES256',
    'kid': APP_STORE_CONNECT_IAP_KEY_ID,
    'typ': 'JWT'
    }

    global issue_time, expiration_time
    issue_time = round(time.time())
    expiration_time = issue_time + 60 * 60 # 1 hour expiration

    payload = {
    'iss': APP_STORE_CONNECT_IAP_ISSUER_ID,
    'iat': issue_time,
    'exp': expiration_time,
    'aud': 'appstoreconnect-v1',
    'nonce': str(uuid.uuid4()),
    'bid': APP_STORE_CONNECT_BUNDLE_ID
    }

    token = jwt.encode(headers=header, payload=payload, key=APP_STORE_CONNECT_IAP_PRIVATE_KEY, algorithm="ES256")
    global jwt_token
    jwt_token = token
    return token

